import React, { useState } from 'react';
import PlayerList from './components/PlayerList';
import Team from './components/Team';
import './styles/App.css';
import playersData from './data/playersData';

function App() {
  const [team, setTeam] = useState([]); // Selected team

  // Function to add a player to the team
  const addPlayerToTeam = (player) => {
    if (team.length < 11 && !team.includes(player)) {
      setTeam([...team, player]);
    }
  };

  return (
    <div>
      <h1>Team Builder</h1>
      <PlayerList players={playersData} addPlayerToTeam={addPlayerToTeam} team={team} />
      <Team team={team} />
    </div>
  );
}

export default App;
